import React, { Component } from 'react';
import Annotation from 'react-image-annotation';
import img from '../ui/assets/images/brom.jpeg';
//import three-js from package
import * as THREE from 'three';
import {
    PointSelector,
    RectangleSelector,
    OvalSelector
  } from 'react-image-annotation/lib/selectors';


  const Box = ({ children, geometry, style }) => (
    <div
      style={{
        ...style,
        position: 'absolute',
        left: `${geometry.x}%`,
        top: `${geometry.y}%`,
        height: `${geometry.height}%`,
        width: `${geometry.width}%`,
      }}
    >
      {children}
    </div>
  )


class SimpleAnnotation extends Component {

	componentDidMount(){
    const width = this.mount.clientWidth
    const height = this.mount.clientHeight
    //ADD SCENE
    this.scene = new THREE.Scene()
    //ADD CAMERA
    this.camera = new THREE.PerspectiveCamera(
      75,
      width / height,
      0.1,
      1000
    )
    this.camera.position.z = 4
    //ADD RENDERER
    this.renderer = new THREE.WebGLRenderer({ antialias: true })
    this.renderer.setClearColor('#000000')
    this.renderer.setSize(width, height)
    this.mount.appendChild(this.renderer.domElement)
    //ADD CUBE
    const geometry = new THREE.BoxGeometry(1, 1, 1)
    const material = new THREE.MeshBasicMaterial({ color: '#433F81'     })
    this.cube = new THREE.Mesh(geometry, material)
    this.scene.add(this.cube)
this.start()
  }

  componentWillUnmount(){
    this.stop()
    this.mount.removeChild(this.renderer.domElement)
  }


    state = {
      annotations: [],
      annotation: {}
    }

    onChange = (annotation) => {
      this.setState({ annotation })
    }

    onSubmit = (annotation) => {
      const { geometry, data } = annotation

      this.setState({
        annotation: {},
        annotations: this.state.annotations.concat({
          geometry,
          data: {
            ...data,
            id: Math.random()
          }
        })
      })
    }

    render () {
      return (
          <Annotation
            src={img}
            alt='Two pebbles anthropomorphized holding hands'

            annotations={this.state.annotations}

            type={RectangleSelector.TYPE}
            value={this.state.annotation}
            renderHighlight={renderHighlight}
            onChange={this.onChange}
            onSubmit={this.onSubmit}
          />
      )
    }
  }

  function renderHighlight ({ annotation, active }) {
    const { geometry } = annotation
    if (!geometry) return null

    return (
      <Box
        key={annotation.data.id}
        geometry={geometry}
        style={{
          border: 'solid 2px black',
          boxShadow: active
            && '0 0 20px 20px rgba(255, 255, 255, 0.3) inset'
        }}
      >
      </Box>
    )
  }

  export default SimpleAnnotation
