export const mainStyle = {
  overflowY: 'scroll',
  overflowX: 'scroll',
  height: '100%',
  width: '100%',
  // height: '500px',
  // width: '900px',
  position: 'relative'
};
export const containerStyle = {
  width: '900px',
  marginLeft: 0,
}
export const rowStyle = {
  flex: 1.0,
  position: 'relative',
};
export const cardStyle = {
  flex: 0.10,
  position: 'relative',
};

export const buttonGroup = {
  size: "sm",
  vertical: false,
  flex: 1.0,
  padding: 15
}

export const mainView = {
  flex: 1,
  flexDirection: 'column',
  justifyContent: 'center',
  maxWidth: '20%'
}

export const innerView = {
  flex: 0.8,
  flexDirection: 'row'
}

export const container = {
  fluid: true,
  maxWidth: '100%',
}

export const row = {
  noGutters: false,
  form: false,
  xs: "2"
}

export const col = {
  xs: "2"
}

export const card = {
  justify:'center',
  alignContent: 'center',
  alignItems: 'center',
  textAlign: 'center',
  width: '100%',
  fluid: true,
  fontSize: "12px"
}

export const cardBody = {
  width: '85%',
  height: '100%'
}

export const mainImg = {
  width: '100%'
}

export const axesImg = {
  width: '100%'
}

export const canvasStyle = {
  position: 'center',
  height: '100px',
  width: '88px',
}
